mapper.insert:
INSERT  INTO
    tb_article_tags(
    article_id,
    tags_id
)
VALUES(
          #{articleId},
          #{tagsId}
      )

mapper.select:
SELECT COUNT(1) FROM tb_article_tags WHERE 1=1

    AND article_id = #{articleId}


    AND tags_id = #{tagsId}

SELECT t.name
FROM tb_article_tags at,
     tb_tags t
WHERE at.article_id = #{articleId}
  AND t.id = at.tags_id;
